import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import Categories from './components/Categories'
import TaskContainer from './components/TaskContainer'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    displayedTasks: [],
    selectedFilter: 'All'
  }

  filterTasks = (category) => {
    let selectedFilter = category
    if (category === 'All') {
      let displayedTasks = [...this.state.tasks]
      this.setState({ displayedTasks, selectedFilter })
    }
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <Categories categories={CATEGORIES} filterTasks={this.filterTasks} selectedFilter={this.state.selectedFilter}/>
        <TaskContainer tasks={this.state.tasks}/>
      </div>
    );
  }
}


export default App;
