import React from 'react';

const Categories = ({categories, filterTasks}) => {

    const buildButtons = () => {
        return categories.map(category => {
            return(
            <button>{category}</button>
            )
        })
    }
    return(
        <div className='categories' onClick={(e) => filterTasks(e)}>
            <h5>Category Filters</h5>
            {
                buildButtons()
            }
        </div>
        )
}

export default Categories
